mapper.select:
select medicine_id medicineId, medicine_name medicineName, medicine_price medicinePrice, medicine_origin_place medicineOriginPlace, medicine_effect medicineEffect, medicine_sp medicineSp, medicine_img medicineImg, medicine_inventory medicineInventory from t_medicine where 1=1
and medicine_id=#{medicineId}
and medicine_name=#{medicineName}
and medicine_origin_place like concat(concat('%',#{medicineOriginPlace}),'%')
and medicine_effect like concat(concat('%',#{medicineEffect}),'%')
